mapper.insert:
/*
CommunityPostMapper.xml
insertPost
커뮤니티 게시글 저장
*/
INSERT INTO community_post (
    user_id, category, title, content, content_type, view_count, like_count, comment_count,
    is_pinned, pin_order, create_date, create_user_id, update_date, update_user_id
) VALUES (
    #{userId}, CAST(#{category} AS community_category_enum), #{title}, #{content}, #{contentType}, #{viewCount}, #{likeCount}, #{commentCount},
    #{isPinned}, #{pinOrder}, CURRENT_TIMESTAMP, #{createUserId}, CURRENT_TIMESTAMP, #{createUserId}
)
RETURNING post_id

mapper.update:
/*
CommunityPostMapper.xml
deleteById
게시글 삭제 (논리 삭제)
*/
UPDATE community_post SET
    delete_date = CURRENT_TIMESTAMP,
    delete_user_id = #{deleteUserId}
WHERE post_id = #{postId}
